class Solution:
    def getEquilibriumPoint(self,arr):
        sum_left = 0
        total = sum(arr)
        for i in range(len(arr)):
            sum_right = total - sum_left - arr[i]
            
            if sum_left == sum_right:
                return i
                
            else:
                sum_left += arr[i]

    
        return -1  

                
        

